	},
	["init"] = {
	["custom"] = "--> We'll initialize DATA after handling an event from DATA WA\nlocal DATA = {}\n\nlocal INVALID_EVENTS = {\n    SPELL_DISPEL            = true,\n    SPELL_DISPEL_FAILED     = true,\n    SPELL_STOLEN            = true,\n    SPELL_AURA_BROKEN       = true,\n    SPELL_AURA_BROKEN_SPELL = true,\n    SPELL_CAST_FAILED       = true,\n    SPELL_PERIODIC_HEAL     = true,\n    SPELL_CAST_SUCCESS      = true,\n}\n\nlocal debug = aura_env.config.debug\nlocal debugPrint = function(...)\n    if not debug then return end\n    print(...)\nend\n\n\nlocal copyData = function(linked_data, handlers_data)\n    for i, v in pairs(linked_data) do\n        if not handlers_data[i] then\n            handlers_data[i] = v\n        end\n    end\nend\n\nlocal setData = function(link_to_data)\n    if link_to_data then\n        local data_tables = { 'ITEMS', 'COOLDOWNS', 'GEMS', 'ENCHANTS', 'NO_COOLDOWN_ITEMS', 'EXTRA_PROCS' }\n        for _, tName in ipairs(data_tables) do\n            if link_to_data[tName] then\n                DATA[tName] = DATA[tName] or {}\n                copyData(link_to_data[tName] , DATA[tName])\n            end\n        end\n        return true\n    end\nend\n\nlocal getData = function()\n    return DATA\nend\n\n--> Putting Global functions to local scope\nlocal tinsert, stformat, substr = table.insert, string.format, string.sub\n\n\n--> Setting constants\nlocal WA_MAIN_EVENT = 'WA_MERFIN_EQUIPPED_ITEMS'\nlocal HEX_NO_GUID = \"0x0000000000000000\"\nlocal MY_GUID = UnitGUID(\"player\")\n\nlocal backend = aura_env\n\n-- Main Table with full info about item\nbackend.items = {\n    --[[\n    [itemId] = {\n        equipmentSlot = number,\n        backend.procs = table,\n        isProc = boolean,\n        itemTexture = string, (constant)\n        procTexture = string,\n        stacks = number,\n        cooldown = number, (constant)\n        duration = number, (as cooldown duration)\n        expirationTime = number, (as cooldown expTime)\n        procDuration = number, (as proc duration)\n        procExpirationTime = number, (as proc expTime)\n        isEnchant = boolean,\n        extraProcId = number,\n        enchantId = number,\n        metaGem = number,\n    }\n    ]]\n}\n\n-- Secondary Table with Equipment Slots ids info\nbackend.equipmentSlots = {\n    -- [equipmentSlot] = itemId,\n}\n\n-- Secondary Table with Main Procs ids info\nbackend.procs = {\n    -- [procId] = itemId,\n}\n\n-- Secondary Table with Extra Procs ids info\nbackend.extraProcs = {\n    -- [extraProcId] = itemId,\n}\n\n--> :Base[Druid] helpful funcs\n\nlocal SendEvent = function(event, ...)\n    WeakAuras.ScanEvents(WA_MAIN_EVENT, event, ...)\nend\n\n-- @returns table - numbers\nlocal GetValuesTable = function(value)\n    local t = {}\n    if type(value) == \"table\" then\n        for _, v in pairs(value) do\n            tinsert(t, v)\n        end\n    elseif type(value) ~= \"table\" then\n        tinsert(t, value)\n    end\n    return t\nend\n\n-- @returns number - itemId, number - enchantId\nlocal GetSlotItemInfo = function(slotID)\n    -- Get the item link of the equipped item in the specified slot\n    local itemLink = GetInventoryItemLink(\"player\", slotID)\n    if itemLink then\n        -- Extract the item string from the item link\n        local itemString = string.match(itemLink, \"item:([%-?%d:]+)\")\n        if itemString then\n            -- Split the item string into its components\n            local itemComponents = {strsplit(\":\", itemString)}\n            -- The 1st component of the item string is the item ID\n            local itemID = tonumber(itemComponents[1])\n            -- The 2nd component of the item string is the enchant ID\n            local enchantID = tonumber(itemComponents[2])\n            -- The 14th component of the item string is the meta gem ID\n            local metaGemID = tonumber(itemComponents[14])\n            local tinkerId = tonumber(itemComponents[10])\n            return itemID, enchantID, metaGemID, tinkerId\n        end\n    end\n    return nil, nil, nil\nend\n\n\n\n--> Helpful funcs that work with frontend data tables\n\n-- @returns string - custom type names (\"NO_CD\", \"ON_USE\", \"INTERNAL\"), number - cooldown time \nlocal GetCooldownInfo = function(equipmentSlot, procId)\n    local _,_, enable = GetInventoryItemCooldown(\"player\", equipmentSlot)\n    \n    if enable == 1 and not DATA.COOLDOWNS[procId] then\n        return \"ON_USE\", nil\n    end\n    \n    if enable == 0 or DATA.COOLDOWNS[procId] then\n        local cooldown = DATA.NO_COOLDOWN_ITEMS[procId]\n        \n        if cooldown then\n            return \"NO_CD\", 0\n        end\n        return \"INTERNAL\", DATA.COOLDOWNS[procId] or 45\n    end\nend\n\n\nlocal OnUse = function(equipmentSlot, itemId)\n    local startTime, duration = GetInventoryItemCooldown(\"player\", equipmentSlot)\n    if duration > 0 then\n        local expirationTime = startTime + duration\n        if expirationTime > backend.items[itemId].expirationTime then\n            backend.items[itemId].duration = duration\n            backend.items[itemId].expirationTime = expirationTime\n            return true\n        end\n    end\nend\n\nlocal OnExtraProc = function(itemId, extraProcId)\n    local _,_, stacks = WA_GetUnitBuff(\"player\", extraProcId)\n    backend.items[itemId].stacks = stacks\nend\n\nlocal OnProc = function(equipmentSlot, itemId, spellId)\n    \n    local _, icon, stacks, _, buffDuration, buffExpirationTime = WA_GetUnitBuff(\"player\", spellId)\n    \n    if backend.items[itemId].type == \"INTERNAL\" then        \n        local cooldown = backend.items[itemId].cooldown\n        backend.items[itemId].duration = cooldown\n        backend.items[itemId].expirationTime = buffExpirationTime - buffDuration + cooldown\n    end\n    \n    backend.items[itemId].isProc = true\n    backend.items[itemId].procTexture = icon\n    backend.items[itemId].procDuration = buffDuration\n    backend.items[itemId].procExpirationTime = buffExpirationTime\n    backend.items[itemId].stacks = stacks\n    SendEvent('ITEM_CHANGED', equipmentSlot)\nend\n\nlocal InitEquipmentSlot = function(equipmentSlot)\n    local itemId, enchantId, metaGem = GetSlotItemInfo(equipmentSlot)\n    if itemId then\n        -- If Engineering is learnt (slight work around)\n        if WeakAuras.IsSpellKnownForLoad(51306) then\n            if equipmentSlot == 6 then\n                -- Nitro Boosts\n                enchantId = 4223\n            elseif equipmentSlot == 10 then\n                -- Synapse Springs\n                enchantId = 4179\n            end\n        end\n    end\n    -- Initialize if new item was equipped or the item changed its equiptment slot\n    if itemId then\n        local data = getData()\n        local procId = data.ITEMS[itemId] or data.ENCHANTS[enchantId] or data.GEMS[metaGem]\n        -- If the item has any proc or cooldown mechanic \n        if procId or select(3, GetInventoryItemCooldown(\"player\", equipmentSlot)) == 1 then\n            backend.equipmentSlots[equipmentSlot] = itemId\n            -- If item is not new, just update his equipment slot\n            if backend.items[itemId] then\n                backend.items[itemId].equipmentSlot = equipmentSlot\n            else\n                backend.items[itemId] = {\n                    procs = {},\n                    extraProc = data.EXTRA_PROCS[itemId],\n                    itemTexture = GetItemIcon(itemId),\n                    expirationTime = GetTime(),  \n                    equipmentSlot = equipmentSlot \n                }\n                \n                if not procId then\n                    backend.items[itemId].type = \"ON_USE\"\n                    return true\n                end\n                \n                local p = GetValuesTable(procId)\n                \n                for _, procId in pairs(p) do\n                    tinsert(backend.items[itemId].procs, procId)\n                    backend.procs[procId] = itemId\n                    if not backend.items[itemId].type then\n                        local type, cooldown = GetCooldownInfo(equipmentSlot, procId)\n                        \n                        backend.items[itemId].type = type\n                        \n                        if type == \"INTERNAL\" then\n                            backend.items[itemId].cooldown = cooldown\n                        end\n                    end\n                end\n                \n                local extraProcId = data.EXTRA_PROCS[itemId]\n                \n                if extraProcId then\n                    backend.extraProcs[extraProcId] = itemId\n                end\n                if enchantId then\n                    backend.items[itemId].enchantId = enchantId\n                end\n                if metaGem then\n                    backend.items[itemId].metaGem = metaGem\n                end\n            end\n            return true\n        end\n    end\n    \n    -- Otherwise set equipment slot to nil (it can be either empty or its item is not in the data)\n    if backend.equipmentSlots[equipmentSlot] then\n        backend.equipmentSlots[equipmentSlot] = nil\n    end\nend\n\n--> Frontend Initialize\n\nlocal frontend = {}\n\nfrontend.arrays = {\n    \n    enchants = {\n        [1] =  4099,  -- Landslide\n        [2] =  4097,  -- Power Torrent\n        [3] =  4083,  -- Hurricane\n        [4] =  4084,  -- Heartsong\n        [5] =  4115,  -- Lightweave Embroidery\n        [6] =  4118,  -- Swordguard Embroidery\n        [7] =  4116,  -- Darkglow Embroidery\n        [8] =  4175,  -- Gnomish X-Ray Scope\n        [9] =  4223,  -- Nitroboosts\n        [10] = 4179,  -- Synapse Springs\n        [11] = 3368,  -- Fallen Crusader\n    },\n    \n    metaGems = {\n        [1] = 41385, -- Invigorating Earthsiege Diamond\n        [2] = 41400, -- Thundering Skyflare Diamond\n        [3] = 41401, -- Insightful Earthsiege Diamond\n    },\n    \n}\n\nfrontend.GetConfig = function(link_to_frontend)\n    \n    local config = link_to_frontend.config\n    \n    -- Creating Settings where we save all individual config options\n    local settings = {\n        --[[\n        equipmentSlots = {\n            [slotId] = true,\n        },\n        enchants = {\n            [enchantId] = true,\n        },\n        gems = {\n            [gemId] = true,\n        },\n        orders = {\n            [slotId] = index,\n        },\n        disabledItems = {\n            [itemId] = true,\n        },\n        disabledGlows = {\n            [itemId] = true,\n        },\n        ]]\n    }\n    \n    -- Config: Display Options Group\n    local displayOpt = config.display\n    \n    settings.showAlways = displayOpt.showAlways\n    settings.showProc = displayOpt.showProc\n    settings.showCd = displayOpt.showCd\n    settings.itemTextureAlways = displayOpt.itemTextureAlways\n    settings.glowReadyToUse = displayOpt.glowReadyToUse\n    settings.glowProc = displayOpt.glowProc\n    settings.disableGlowNoCd = displayOpt.disableGlowNoC\n    \n    -- Config: Display Options Group. Equipment Slots\n    for id, enabled in ipairs(displayOpt.equipmentSlots) do\n        if enabled then\n            settings.equipmentSlots = settings.equipmentSlots or {}\n            settings.equipmentSlots[id] = true\n        end\n    end\n    \n    \n    -- Config: Display Options Group. Enchants\n    settings.enchants = {}\n    for i, enabled in ipairs(displayOpt.validEnchants) do\n        if enabled then\n            local id = frontend.arrays.enchants[i]\n            settings.enchants[id] = true\n        end\n    end\n    \n    -- Config: Display Options Group. Meta Gems\n    settings.gems = {}\n    for i, enabled in ipairs(displayOpt.validMetaGems) do\n        if enabled then\n            local id = frontend.arrays.metaGems[i]\n            settings.gems[id] = true\n        end\n    end\n    \n    -- Config: Display Options Group. Order\n    settings.orders = {}\n    for slotIdStr, index in pairs(displayOpt.order) do\n        local slotId = tonumber(slotIdStr)\n        settings.orders[slotId] = index\n    end\n    \n    -- Config: Advanced Options Group\n    local advancedOpt = config.advanced\n    \n    settings.disabledItems = {}\n    settings.disabledGlows = {}\n    for _, data in ipairs(advancedOpt) do\n        local itemId = data.itemId\n        if data.disable then\n            settings.disabledItems[itemId] = true\n        end\n        if data.disableGlow then\n            settings.disabledGlows[itemId] = true\n        end\n    end\n    \n    link_to_frontend.settings = settings\n    return true\nend \n\nfrontend.Register = function(link_to_frontend)\n    -- Need to be reviewed later\n    if frontend.GetConfig(link_to_frontend) then\n        return true\n    end\nend\n\nlocal LoadLastSession = function(equipmentSlot, itemId)\n    backend.saved = backend.saved or {}\n    local db = backend.saved[WeakAuras.me]\n    if not db then return end\n    \n    if db[itemId] then\n        local expirationTimeOS = db[itemId].expirationTimeOS\n        local cooldown = expirationTimeOS - time()\n        return cooldown\n    end\nend\n\nlocal SaveLastSession = function()\n    backend.saved = backend.saved or {}\n    backend.saved[WeakAuras.me] = {}\n    local db = backend.saved[WeakAuras.me]\n    for itemId, itemData in pairs(backend.items) do\n        if itemData.type == 'INTERNAL' or itemData.type == 'NO_CD' then\n            local expirationTime = itemData.expirationTime\n            db[itemId] = db[itemId] or {}\n            db[itemId].expirationTimeOS = expirationTime - GetTime() + time()\n        end\n    end\nend\n\nbackend.Init = function(link_to_data)\n    \n    if not setData(link_to_data) then return end\n    \n    local anyEquipmentSlot = false\n    for equipmentSlot = 1, 18 do\n        if InitEquipmentSlot(equipmentSlot) then\n            anyEquipmentSlot = true\n        end\n    end\n    if anyEquipmentSlot then\n        -- Check if new registered items have any proc or cooldown in aura_env.saved\n        for equipmentSlot, itemId in pairs(backend.equipmentSlots) do\n            local isBuffed = false\n            for _, procId in ipairs(backend.items[itemId].procs) do\n                if WA_GetUnitBuff(\"player\", procId) then\n                    isBuffed = true\n                    OnProc(equipmentSlot, itemId, procId)\n                    if backend.items[itemId].extraProc then\n                        OnExtraProc(itemId, backend.items[itemId].extraProc)\n                    end\n                    break\n                end\n            end\n            \n            -- Item item doesn't have an active proc\n            if not isBuffed then\n                local itemData = backend.items[itemId]\n                itemData.isProc = false\n                itemData.stacks = 0\n                \n                if itemData.type == 'INTERNAL' or itemData.type == 'NO_CD' then\n                    local savedCooldown = LoadLastSession(equipmentSlot, itemId)\n                    if savedCooldown and savedCooldown > 0 then\n                        itemData.duration = itemData.cooldown or type == \"NO_CD\" and 30\n                        itemData.expirationTime = savedCooldown + GetTime()\n                    end\n                elseif itemData.type == \"ON_USE\" then\n                    OnUse(equipmentSlot, itemId)  \n                end\n            end\n        end\n    end\n    \n    backend.initialized = true\n    --WeakAuras.timer:ScheduleTimer(SendEvent, 0.1, 'BACKEND_INITIALIZED', backend)\n    SendEvent('BACKEND_INITIALIZED', backend)\nend\n\n--> Events Handler\n\nlocal OnEvent = {}\n\nOnEvent['OPTIONS'] = function()\n    backend.initialized = false\n    SaveLastSession()\nend\n\nOnEvent['STATUS'] = function()\nend\n\nOnEvent['COMBAT_LOG_EVENT_UNFILTERED'] = function(_, subEvent, _, sourceGUID, _,_,_, destGUID, _,_,_, spellId)\n    if not subEvent then return end\n    if INVALID_EVENTS[subEvent] then return end\n    if ((destGUID == MY_GUID and ((not sourceGUID or sourceGUID == HEX_NO_GUID) or sourceGUID == destGUID)) or sourceGUID == MY_GUID) then\n        if backend.procs[spellId] then\n            \n            if spellId == 89091 and subEvent == 'SPELL_DAMAGE' then\n                return\n            end\n            \n            local itemId = backend.procs[spellId]\n            local equipmentSlot = backend.items[itemId].equipmentSlot\n            local type = backend.items[itemId].type\n            \n            if GetInventoryItemID(\"player\", equipmentSlot) ~= itemId then return end\n            if subEvent == \"SPELL_AURA_APPLIED\" \n            or subEvent == \"SPELL_AURA_REFRESH\" \n            or subEvent == \"SPELL_AURA_APPLIED_DOSE\" \n            or subEvent == \"SPELL_AURA_REMOVED_DOSE\" then\n                OnProc(equipmentSlot, itemId, spellId)\n                SendEvent('ITEM_CHANGED', equipmentSlot)\n                \n            elseif subEvent == \"SPELL_AURA_REMOVED\" then\n                backend.items[itemId].isProc = false\n                backend.items[itemId].stacks = 0\n                \n                if type == \"ON_USE\" then\n                    OnUse(equipmentSlot, itemId)\n                    \n                elseif type == \"NO_CD\" then\n                    backend.items[itemId].duration = 0\n                    backend.items[itemId].expirationTime = GetTime()\n                end\n                \n            elseif type == \"INTERNAL\" then\n                backend.items[itemId].isProc = false\n                backend.items[itemId].duration = backend.items[itemId].cooldown\n                backend.items[itemId].expirationTime = backend.items[itemId].duration + GetTime()\n            end\n            \n            SendEvent('ITEM_CHANGED', equipmentSlot)\n            return true\n        end\n        \n        if backend.extraProcs[spellId] then\n            \n            if subEvent == \"SPELL_AURA_APPLIED\" \n            or subEvent == \"SPELL_AURA_REFRESH\" \n            or subEvent == \"SPELL_AURA_APPLIED_DOSE\" \n            or subEvent == \"SPELL_AURA_REMOVED_DOSE\"\n            or subEvent == \"SPELL_AURA_REMOVED\" then\n                \n                local itemId = backend.extraProcs[spellId]\n                local equipmentSlot = backend.items[itemId].equipmentSlot\n                \n                OnExtraProc(itemId, spellId)\n                SendEvent('ITEM_CHANGED', equipmentSlot)\n                return true\n                \n            end\n        end\n    end\n    \nend\n\nOnEvent['PLAYER_EQUIPMENT_CHANGED'] = function(equipmentSlot, isEmpty)\n    \n    local timestamp = GetTime()\n    \n    if not isEmpty and InitEquipmentSlot(equipmentSlot) then\n        \n        local itemId = backend.equipmentSlots[equipmentSlot]\n        local type = backend.items[itemId].type\n        \n        backend.items[itemId].icon = backend.items[itemId].itemTexture\n        backend.items[itemId].stacks = 0\n        backend.items[itemId].isProc = false\n        \n        if type == \"ON_USE\" then\n            \n            OnUse(equipmentSlot, itemId)\n            \n        elseif type == \"INTERNAL\" then\n            local currentCooldown = backend.items[itemId].expirationTime - timestamp\n            \n            if currentCooldown > 30 then\n                backend.items[itemId].duration = backend.items[itemId].cooldown\n                backend.items[itemId].expirationTime = timestamp + backend.items[itemId].cooldown\n                \n            elseif currentCooldown < backend.items[itemId].cooldown then\n                backend.items[itemId].duration = backend.items[itemId].cooldown\n                backend.items[itemId].expirationTime = timestamp + backend.items[itemId].cooldown\n                \n            elseif currentCooldown > 30 then\n                backend.items[itemId].duration = backend.items[itemId].cooldown\n                backend.items[itemId].expirationTime = savedCooldown + timestamp\n            end\n            \n        elseif type == \"NO_CD\" then\n            \n            if equipmentSlot ~= 18 then\n                backend.items[itemId].duration = 30\n                backend.items[itemId].expirationTime = timestamp + 30\n            end\n            \n        end\n        \n    else\n        backend.equipmentSlots[equipmentSlot] = nil\n    end\n    \n    SendEvent('ITEM_CHANGED', equipmentSlot)\nend\n\nOnEvent['BAG_UPDATE_COOLDOWN'] = function()\n    for equipmentSlot in pairs(backend.equipmentSlots) do\n        local itemId = GetInventoryItemID(\"player\", equipmentSlot)\n        if itemId and itemId == backend.equipmentSlots[equipmentSlot]\n        and backend.items[itemId].type == \"ON_USE\"\n        and OnUse(equipmentSlot, itemId) then\n            backend.items[itemId].stacks = 0\n            SendEvent('ITEM_CHANGED', equipmentSlot)\n        end\n    end\nend\n\nOnEvent['PLAYER_LEAVING_WORLD'] = function()\n    SaveLastSession()\nend\n\nOnEvent[WA_MAIN_EVENT] = function(subEvent, ...)\n    if subEvent == 'DATA_INIT' then\n        backend.Init(...)\n        \n    elseif subEvent == 'REGISTER_FRONTEND' then\n        local link_to_frontend = ...\n        if frontend.Register(link_to_frontend) then\n            SendEvent('REGISTER_FRONTEND_FINISHED', link_to_frontend.id)\n        end\n    end\nend\n\nbackend.OnBackendEvent = function(e, ...)\n    if e == WA_MAIN_EVENT or backend.initialized then\n        return OnEvent[e](...)\n    end\nend\n\nlocal BuildState = function(settings, itemData, itemId)\n    \n    local isProc = itemData.isProc\n    local state = {}\n    state.progressType = 'timed'\n    state.changed = true\n    state.autoHide = not settings.showAlways\n    state.duration = (isProc and settings.showProc) and itemData.procDuration or itemData.duration or 0\n    state.expirationTime = (isProc and settings.showProc) and itemData.procExpirationTime or itemData.expirationTime or GetTime()\n    state.icon = (isProc and settings.showProc and not settings.itemTextureAlways) and itemData.procTexture or itemData.itemTexture\n    state.stacks = (isProc and settings.showProc) and itemData.stacks or 0\n    state.isEnchant = itemData.isEnchant\n    state.itemType = itemData.type\n    state.itemId = itemId\n    state.isProc = isProc\n    state.equipmentSlot = itemData.equipmentSlot\n    state.showProc = settings.showProc\n    state.disableGlowNoCd = settings.disableGlowNoCd\n    state.disabledGlow = settings.disabledGlows[itemId]\n    state.index = settings.orders[itemData.equipmentSlot]\n    \n    local equimentSlotCondition = settings.equipmentSlots[itemData.equipmentSlot]\n    local enchantCondition = not itemData.enchantId or not DATA.ENCHANTS[itemData.enchantId] or settings.enchants[itemData.enchantId]\n    local gemCondition = not itemData.metaGem or not DATA.GEMS[itemData.metaGem] or settings.gems[itemData.metaGem]\n    local isEnabled = not settings.disabledItems[itemId]\n    \n    if equimentSlotCondition and enchantCondition and gemCondition and isEnabled  then\n        if settings.showAlways then\n            state.show = true\n        elseif settings.showCd then\n            state.show = GetTime() < itemData.expirationTime\n        elseif settings.showProc then\n            state.show = isProc\n        end\n    else\n        state.show = false\n    end\n    \n    return state\nend\n\nbackend.OnFrontendEvent = function(a, subEvent, link_to_frontend, ...)\n    \n    local settings = link_to_frontend.settings\n    \n    if subEvent == 'REGISTER_FRONTEND_FINISHED' then\n        local frontendId = ...\n        if frontendId == link_to_frontend.id then\n            local anyState = false\n            for equipmentSlot, itemId in pairs(backend.equipmentSlots) do\n                if settings.equipmentSlots[equipmentSlot] then\n                    local itemData = backend.items[itemId]\n                    local state = BuildState(settings, itemData, itemId)\n                    a[equipmentSlot] = state\n                    anyState = true\n                end\n            end\n            return anyState\n        end\n    elseif subEvent == 'ITEM_CHANGED' then\n        local equipmentSlot = ...\n        if not backend.equipmentSlots[equipmentSlot] then\n            if a[equipmentSlot] then\n                a[equipmentSlot].show = false\n                a[equipmentSlot].changed = true\n                return true\n            end\n        else\n            local itemId = backend.equipmentSlots[equipmentSlot]\n            local itemData = backend.items[itemId]\n            local state = BuildState(settings, itemData, itemId)\n            a[equipmentSlot] = state\n            return true\n        end\n    end\nend",
